import {SchemeValue} from "./SchemeValue";
/**
 * http://xxxxxx.com/wiki/Uniform_Resource_Identifier
 *
 * ```
 *    urn:example:mammal:monotreme:echidna
 *    ??? ????????????????????????????????
 *   scheme             path
 * ```
 */
export interface IUrn {

	/**
	 * The scheme name for this URI.
	 */
	scheme?: SchemeValue; // string literal

	/**
	 * The absolute path of the URI.
	 */
	path?: string;

}

/**
 * http://xxxxxx.com/wiki/Uniform_Resource_Identifier
 * scheme:[//[user:password@]domain[:port]][/]path[?query][#fragment]
 *
 * ```
 *                      hierarchical part
 *          ???????????????????????????????????????????
 *                      authority               path
 *          ???????????????????????????????????????????
 *    abc://username:my@my.com:123/path/data?key=value#fragid1
 *    ???   ????????????????? ??????????? ???           ????????? ???????
 *  scheme  user information     host     port            query   fragment
 * ```
 */
export interface IUri extends IUrn {


	/**
	 * The user name, password, or other user-specific information associated with the specified URI.
	 */
	userInfo?: string;

	/**
	 * The host component of this instance.
	 */
	host?: string;

	/**
	 * The port number of this URI.
	 */
	port?: number;

	/**
	 * Gets any query information included in the specified URI.
	 */
	query?: string;

	/**
	 * The escaped URI fragment.
	 */
	fragment?: string;


}

export default IUri;
